import { Header } from "./components/todoList/Header";
import { TodoList } from "./components/todoList/TodoList";
import { useDispatch, useSelector } from "react-redux";
import { getVisibleTodos } from "./redux/filter";
import { fetchTodos } from "./redux/todos/action";
import "./index.css";
import { useEffect } from "react";
import {Container, Box, makeStyles, useTheme} from "@material-ui/core";


const useStyles = makeStyles((them)=>({
  root:{
    flexGrow:1
  },
  todoList:{
    border:1
  }
}))

export const App = () => {
  const classes = useStyles()
  const dispatch = useDispatch();
  const filter = useSelector((state) => state.visibilityFilter);
  const todos = useSelector((state) => state.todos);

  const visibleTodos = getVisibleTodos(todos, filter);

  useEffect(() => {
    dispatch(fetchTodos());
  }, []);

  return (
    <Container>
      <Box className={classes.todoList}>
        <Header />
        <TodoList filter={filter} todos={todos} visibleTodos={visibleTodos} />
      </Box>
      </Container>
  );
};
