import React from "react";
import { BtnDelete } from "./ButtonDelete";
import {deleteTodo, toggleTodo} from "../../redux/todos/action";
import { useDispatch } from "react-redux";
import {Button, Container,Grid} from "@material-ui/core";
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

export const ListItem = ({ todo }) => {
  const dispatch = useDispatch();
  return (
   <Grid container
         alignItems="center" justifyContent='space-between' className='listItem'>
      <li
        className={todo.isDone ? "done" : "notDone"}
        onClick={() => dispatch(toggleTodo(todo))}
      >
        {todo.value}
      </li>
        <IconButton aria-label='delete'>
            <DeleteIcon fontSize='small' onClick={() => dispatch(deleteTodo(todo))}/>
        </IconButton>
   </Grid>
  );
};
